let emails = document.querySelectorAll('.gv_hide-email'),
    user = emails[0].dataset.user,
    site = emails[0].dataset.site;
emails.forEach((e) => {
    e.dataset.site
        ? e.setAttribute(
              'href',
              'mailto:' + e.dataset.user + '@' + e.dataset.site
          )
        : e.setAttribute('href', 'mailto:' + user + '@' + site),
        e.hasAttribute('data-keep-text') || (e.innerHTML = user + '@' + site);
});
